import React, { useEffect, useState } from "react";
import axios from "axios";

const Events = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/events");
        setEvents(response.data);
      } catch (err) {
        setError("Failed to load events.");
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, []);

  const handleBookEvent = async (eventId) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("You need to be logged in to book an event.");
      return;
    }

    try {
      await axios.post(
        `http://localhost:5000/api/events/${eventId}/book`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Event booked successfully!");
    } catch (err) {
      alert("Failed to book event.");
    }
  };

  if (loading) return <p>Loading events...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="container mt-4">
      <h2 className="text-center">Upcoming Events</h2>
      <div className="row">
        {events.length > 0 ? (
          events.map((event, index) => (
            <div key={event._id} className="col-md-4 mb-4">
              <div className="card">
                <img
                  src={event.imageUrl || getDefaultImage(index)}
                  className="card-img-top"
                  alt={event.name}
                  style={{ height: "200px", objectFit: "cover" }}
                />
                <div className="card-body">
                  <h5 className="card-title">{event.name}</h5>
                  <p className="card-text">{event.description}</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleBookEvent(event._id)}
                  >
                    Book Now
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">No events available.</p>
        )}
      </div>
    </div>
  );
};

// Default event images
const getDefaultImage = (index) => {
  const images = [
    "https://example.com/music-concert.jpg",
    "https://example.com/tech-conference.jpg",
    "https://example.com/art-exhibition.jpg",
    "https://example.com/sports-tournament.jpg",
    "https://example.com/food-festival.jpg",
    "https://example.com/startup-meetup.jpg"
  ];
  return images[index % images.length];
};

export default Events;
