import React, { useState, useEffect } from "react";
import "./Events.css";
import { Link } from "react-router-dom";
import UserProfileModel from "./UserProfileModel";

function Events() {
  const [events, setEvents] = useState([]);
  const [showUserProfileModal, setShowUserProfileModal] = useState(false);
  const [user, setUser] = useState();
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchEvents();
  }, []);

  const openUserProfileModal = () => {
    const userData = JSON.parse(localStorage.getItem("user"));
    setUser(userData);
    setShowUserProfileModal(true);
  };

  const closeUserProfileModal = () => {
    setShowUserProfileModal(false);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  function formatDate(dateString) {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  }

  const fetchEvents = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/event/all", {
        headers: {
          Authorization: `${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();
        setEvents(data); 
      } else {
        console.error("Failed to fetch events.");
      }
    } catch (error) {
      console.error("Error during fetchEvents:", error);
    }
  };

  return (
    <div className="events">
      {/* Navbar from Dashboard */}
      <nav className="navbar">
        <div className="left">
          <Link to="/dashboard">
            <img src="your-logo.png" alt="Logo" className="logo" />
            <span>Smart Events</span>
          </Link>
        </div>
        <div className="right">
          <button onClick={toggleDropdown}>Profile â–¼</button>
          {dropdownOpen && (
            <div className="dropdown-menu">
              <button onClick={openUserProfileModal}>View Profile</button>
              <button onClick={() => localStorage.clear()}>Logout</button>
            </div>
          )}
        </div>
      </nav>

      {/* Events Section */}
      <div className="events-container">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event._id} className="event-card">
              <img src={event.image} alt={event.name} className="event-image" />
              <h3>{event.name}</h3>
              <p>{event.description}</p>
              <p>Date: {formatDate(event.date)}</p>
              <button onClick={() => alert(`Booked: ${event.name}`)}>Book Now</button>
            </div>
          ))
        ) : (
          <p>No events available</p>
        )}
      </div>

      {showUserProfileModal && <UserProfileModel user={user} onClose={closeUserProfileModal} />}
    </div>
  );
}

export default Events;
