import { useState } from "react";

const eventsData = [
  {
    id: 1,
    name: "Music Fest 2025",
    description: "An electrifying night with top artists and DJs.",
    image: "https://via.placeholder.com/300x200?text=Music+Fest",
    price: 50,
  },
  {
    id: 2,
    name: "Tech Conference",
    description: "Explore the latest in AI, Blockchain, and more.",
    image: "https://via.placeholder.com/300x200?text=Tech+Conference",
    price: 100,
  },
  {
    id: 3,
    name: "Food Carnival",
    description: "A paradise for food lovers with global cuisines.",
    image: "https://via.placeholder.com/300x200?text=Food+Carnival",
    price: 20,
  },
  {
    id: 4,
    name: "Comedy Night",
    description: "Laugh your heart out with top comedians.",
    image: "https://via.placeholder.com/300x200?text=Comedy+Night",
    price: 30,
  },
];

export default function EventBooking() {
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [message, setMessage] = useState("");

  const bookTicket = (event) => {
    setSelectedEvent(event);
    setMessage(`Successfully booked for ${event.name}!`);
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold text-center mb-4">Upcoming Events</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        {eventsData.map((event) => (
          <div key={event.id} className="border rounded-lg p-4 shadow-lg">
            <img src={event.image} alt={event.name} className="w-full h-40 object-cover rounded" />
            <h2 className="text-lg font-semibold mt-2">{event.name}</h2>
            <p className="text-gray-600">{event.description}</p>
            <p className="text-green-600 font-bold">${event.price}</p>
            <button
              onClick={() => bookTicket(event)}
              className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              Book Now
            </button>
          </div>
        ))}
      </div>
      {message && (
        <div className="mt-4 p-3 bg-green-100 text-green-700 border border-green-400 rounded">
          {message}
        </div>
      )}
    </div>
  );
}
