import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar"; 
import "./Dashboard.css";
import { Link } from "react-router-dom";
import UserProfileModel from "./UserProfileModel";

function Dashboard() {
  const [userData, setUserData] = useState({});
  const [userEvents, setUserEvents] = useState([]);
  const [scheduledEvents, setScheduledEvents] = useState([]);
  const [showUserProfileModal, setShowUserProfileModal] = useState(false);
  const [user, setUser] = useState(null);
  const token = localStorage.getItem("token");
  const [editingEvent, setEditingEvent] = useState(null);
  const [editedFields, setEditedFields] = useState({});

  useEffect(() => {
    if (token) {
      fetchUserData();
      fetchUserEvents();
      fetchScheduledEvents();
    }
  }, [token]);

  const fetchUserData = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/user", {
        headers: { "Content-Type": "application/json", Authorization: `${token}` },
      });
      if (response.ok) {
        const data = await response.json();
        setUserData(data);
      } else {
        console.error("Error fetching user data");
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  const fetchUserEvents = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/event", {
        headers: { Authorization: `${token}`, "Content-Type": "application/json" },
      });
      if (response.ok) {
        const data = await response.json();
        setUserEvents(data.userEvents);
      } else {
        console.error("Failed to fetch user events.");
      }
    } catch (error) {
      console.error("Error during fetchUserEvents:", error);
    }
  };

  const fetchScheduledEvents = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/ticket/user", {
        headers: { Authorization: `${token}`, "Content-Type": "application/json" },
      });
      if (response.ok) {
        const data = await response.json();
        setScheduledEvents(data.userTickets);
      } else {
        console.error("Error fetching scheduled events");
      }
    } catch (error) {
      console.error("Error fetching scheduled events:", error);
    }
  };

  const handleEditClick = (event) => {
    setEditingEvent(event);
    setEditedFields({ title: event.title, description: event.description, date: event.date, venue: event.venue });
  };

  const handleFieldChange = (field, value) => {
    setEditedFields({ ...editedFields, [field]: value });
  };

  const handleSaveClick = async () => {
    if (!editingEvent) return;
    try {
      const response = await fetch(`https://ticket-a8ez.onrender.com/event/${editingEvent._id}`, {
        method: "PUT",
        headers: { Authorization: `${token}`, "Content-Type": "application/json" },
        body: JSON.stringify(editedFields),
      });

      if (response.ok) {
        setUserEvents((prevUserEvents) =>
          prevUserEvents.map((event) => (event._id === editingEvent._id ? { ...event, ...editedFields } : event))
        );
        setEditingEvent(null);
        setEditedFields({});
      } else {
        console.error("Failed to update event.");
      }
    } catch (error) {
      console.error("Error during event update:", error);
    }
  };

  const handleDeleteClick = async (eventId) => {
    try {
      const response = await fetch(`https://ticket-a8ez.onrender.com/event/${eventId}`, {
        method: "DELETE",
        headers: { Authorization: `${token}` },
      });

      if (response.ok) {
        setUserEvents((prevUserEvents) => prevUserEvents.filter((event) => event._id !== eventId));
      } else {
        console.error("Failed to delete event.");
      }
    } catch (error) {
      console.error("Error during event deletion:", error);
    }
  };

  const handleCancelEventClick = async (eventId) => {
    try {
      const response = await fetch(`https://ticket-a8ez.onrender.com/ticket/${eventId}`, {
        method: "DELETE",
        headers: { Authorization: `${token}` },
      });

      if (response.ok) {
        setScheduledEvents((prevScheduledEvents) =>
          prevScheduledEvents.filter((ticket) => ticket.event?._id !== eventId)
        );
      } else {
        console.error("Failed to cancel event.");
      }
    } catch (error) {
      console.error("Error during event cancellation:", error);
    }
  };

  return (
    <div className="dashboard">
      <Navbar />

      <div className="dashboard-content">
        <h2>Welcome, {userData.name || "User"}</h2>
        <button onClick={() => setShowUserProfileModal(true)}>View Profile</button>

        {showUserProfileModal && (
          <UserProfileModel user={user} onClose={() => setShowUserProfileModal(false)} />
        )}

        <h3>Your Events</h3>
        <ul>
          {userEvents.map((event) => (
            <li key={event._id}>
              {editingEvent?._id === event._id ? (
                <div>
                  <input
                    type="text"
                    value={editedFields.title}
                    onChange={(e) => handleFieldChange("title", e.target.value)}
                  />
                  <input
                    type="text"
                    value={editedFields.description}
                    onChange={(e) => handleFieldChange("description", e.target.value)}
                  />
                  <input
                    type="date"
                    value={editedFields.date}
                    onChange={(e) => handleFieldChange("date", e.target.value)}
                  />
                  <input
                    type="text"
                    value={editedFields.venue}
                    onChange={(e) => handleFieldChange("venue", e.target.value)}
                  />
                  <button onClick={handleSaveClick}>Save</button>
                  <button onClick={() => setEditingEvent(null)}>Cancel</button>
                </div>
              ) : (
                <>
                  <span>{event.title}</span>
                  <button onClick={() => handleEditClick(event)}>Edit</button>
                  <button onClick={() => handleDeleteClick(event._id)}>Delete</button>
                </>
              )}
            </li>
          ))}
        </ul>

        <h3>Scheduled Events</h3>
        <ul>
          {scheduledEvents.map((ticket) => (
            <li key={ticket._id}>
              <span>{ticket.event?.title}</span>
              <button onClick={() => handleCancelEventClick(ticket._id)}>Cancel</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
