import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import UserProfileModel from "./UserProfileModel";
import "bootstrap/dist/css/bootstrap.min.css";
import { Navbar, Nav, Form, FormControl, Button, Dropdown } from "react-bootstrap";
import { Bell, Moon, Sun } from "react-bootstrap-icons";

function Events() {
  const [events, setEvents] = useState([]);
  const [showUserProfileModal, setShowUserProfileModal] = useState(false);
  const [user, setUser] = useState();
  const [searchQuery, setSearchQuery] = useState("");
  const [darkMode, setDarkMode] = useState(false);

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/event/all", {
        headers: { Authorization: `${token}`, "Content-Type": "application/json" },
      });

      if (response.ok) {
        const data = await response.json();
        setEvents(data);
      } else {
        console.error("Failed to fetch events, loading sample events.");
        loadSampleEvents();
      }
    } catch (error) {
      console.error("Error fetching events:", error);
      loadSampleEvents();
    }
  };

  const loadSampleEvents = () => {
    const sampleEvents = [
      {
        _id: "1",
        name: "Tech Conference 2025",
        date: "2025-06-15",
        image: "https://images.pexels.com/photos/3183165/pexels-photo-3183165.jpeg",
        description: "Join industry experts discussing the latest trends in technology.",
      },
      {
        _id: "2",
        name: "Music Fest 2025",
        date: "2025-07-20",
        image: "https://images.pexels.com/photos/1190297/pexels-photo-1190297.jpeg",
        description: "A spectacular music festival featuring top artists from around the world.",
      },
      {
        _id: "3",
        name: "Food Carnival",
        date: "2025-08-10",
        image: "https://images.pexels.com/photos/1640772/pexels-photo-1640772.jpeg",
        description: "Taste delicious cuisines from different cultures in one place.",
      },
      {
        _id: "4",
        name: "Startup Meetup",
        date: "2025-09-05",
        image: "https://images.pexels.com/photos/3184291/pexels-photo-3184291.jpeg",
        description: "Network with entrepreneurs and investors to share innovative ideas.",
      },
    ];
    setEvents(sampleEvents);
  };

  const filteredEvents = events.filter((event) =>
    event.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const openUserProfileModal = () => {
    const userData = JSON.parse(localStorage.getItem("user"));
    setUser(userData);
    setShowUserProfileModal(true);
  };

  const closeUserProfileModal = () => {
    setShowUserProfileModal(false);
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle("dark-mode");
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", { year: "numeric", month: "long", day: "numeric" });
  };

  return (
    <div className={darkMode ? "dark-theme" : ""}>
      {/* Navbar */}
      <Navbar bg="light" expand="lg" className="px-3">
        <Navbar.Brand as={Link} to="/dashboard">
          <img src="your-logo.png" alt="Logo" height="40" />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Form className="d-flex">
              <FormControl
                type="search"
                placeholder="Search Events..."
                className="me-2"
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Nav>
          <Nav>
            <Button variant="outline-secondary" className="me-2">
              <Bell size={20} />
            </Button>
            <Button variant="outline-secondary" className="me-2" onClick={toggleDarkMode}>
              {darkMode ? <Sun size={20} /> : <Moon size={20} />}
            </Button>
            <Dropdown>
              <Dropdown.Toggle variant="primary">Profile</Dropdown.Toggle>
              <Dropdown.Menu align="end">
                <Dropdown.Item onClick={openUserProfileModal}>View Profile</Dropdown.Item>
                <Dropdown.Item onClick={handleLogout}>Logout</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Nav>
        </Navbar.Collapse>
      </Navbar>

      {/* Events Section */}
      <div className="container mt-4">
        <div className="row">
          {filteredEvents.length > 0 ? (
            filteredEvents.map((event) => (
              <div key={event._id} className="col-md-4 mb-4">
                <div className="card">
                  <img src={event.image} alt={event.name} className="card-img-top" />
                  <div className="card-body">
                    <h5 className="card-title">{event.name}</h5>
                    <p className="card-text">{event.description}</p>
                    <p className="card-text"><strong>Date:</strong> {formatDate(event.date)}</p>
                    <Button variant="primary" onClick={() => alert(`Booked: ${event.name}`)}>Book Now</Button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p>No events found</p>
          )}
        </div>
      </div>

      {showUserProfileModal && <UserProfileModel user={user} onClose={closeUserProfileModal} />}
    </div>
  );
}

export default Events;
