import React, { useState, useEffect } from "react";
import "./Events.css";
import { Link } from "react-router-dom";
import UserProfileModel from "./UserProfileModel";
import Navbar from "../components/Navbar"; // Import Navbar

function Events() {
  const [events, setEvents] = useState([]);
  const [showUserProfileModal, setShowUserProfileModal] = useState(false);
  const [user, setUser] = useState();
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/event/all", {
        headers: {
          Authorization: `${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();
        setEvents(data);
      } else {
        console.error("Failed to fetch events.");
      }
    } catch (error) {
      console.error("Error during fetchEvents:", error);
    }
  };

  return (
    <div className="events">
      <Navbar /> {/* Use Navbar component */}

      <div className="events-container">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event._id} className="event-card">
              <img src={event.image} alt={event.name} className="event-image" />
              <h3>{event.name}</h3>
              <p>{event.description}</p>
              <p>Date: {new Date(event.date).toLocaleDateString()}</p>
              <button onClick={() => alert(`Booked: ${event.name}`)}>Book Now</button>
            </div>
          ))
        ) : (
          <p>No events available</p>
        )}
      </div>

      {showUserProfileModal && <UserProfileModel user={user} onClose={() => setShowUserProfileModal(false)} />}
    </div>
  );
}

export default Events;
