import React, { useState, useEffect } from "react";
import "./Events.css";
import { Link } from "react-router-dom";
import Navbar from "./Navbar";  // Import the shared Navbar component
import UserProfileModel from "./UserProfileModel";

function Events() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/event/all");
      if (response.ok) {
        const data = await response.json();
        setEvents(data);
      } else {
        console.error("Failed to fetch events.");
      }
    } catch (error) {
      console.error("Error during fetchEvents:", error);
    }
  };

  return (
    <div className="events">
      <Navbar /> {/* Use the shared Navbar here */}

      <div className="events-container">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event._id} className="event-card">
              <img src={event.image} alt={event.name} className="event-image" />
              <h3>{event.name}</h3>
              <p>{event.description}</p>
              <button onClick={() => alert(`Booked: ${event.name}`)}>Book Now</button>
            </div>
          ))
        ) : (
          <p>No events available</p>
        )}
      </div>
    </div>
  );
}

export default Events;
