import React, { useState, useEffect } from "react";
import "./Events.css";
import { Link } from "react-router-dom";
import UserProfileModel from "./UserProfileModel";

function Events() {
  const [events, setEvents] = useState([]);
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [showAlreadyBookedPopup, setShowAlreadyBookedPopup] = useState(false);
  const [showUserProfileModal, setShowUserProfileModal] = useState(false);
  const [user, setUser] = useState();

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchEvents();
  }, []);

  const openUserProfileModal = () => {
    const userData = JSON.parse(localStorage.getItem("user"));
    setUser(userData);
    setShowUserProfileModal(true);
  };

  const closeUserProfileModal = () => {
    setShowUserProfileModal(false);
  };

  function formatDate(dateString) {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  }

  const fetchEvents = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/event/all", {
        headers: {
          Authorization: `${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();

        // Valid event data with real image sources
        const sampleEvents = [
          {
            _id: "1",
            name: "Tech Conference 2025",
            date: "2025-06-15",
            image: "https://images.unsplash.com/photo-1531297484001-80022131f5a1",
          },
          {
            _id: "2",
            name: "Music Fest 2025",
            date: "2025-07-20",
            image: "https://images.unsplash.com/photo-1519676867240-f03562a6b6b2",
          },
          {
            _id: "3",
            name: "Food Carnival",
            date: "2025-08-10",
            image: "https://images.unsplash.com/photo-1532634922-8a0c79d273d2",
          },
          {
            _id: "4",
            name: "Startup Meetup",
            date: "2025-09-05",
            image: "https://images.unsplash.com/photo-1563986768609-322da13575f3",
          },
          {
            _id: "5",
            name: "Sports Championship",
            date: "2025-10-12",
            image: "https://images.unsplash.com/photo-1517649763962-0c623066013b",
          },
          {
            _id: "6",
            name: "Art Exhibition",
            date: "2025-11-25",
            image: "https://images.unsplash.com/photo-1578926375605-eaf7559f147b",
          },
          {
            _id: "7",
            name: "Coding Hackathon",
            date: "2025-12-10",
            image: "https://images.unsplash.com/photo-1555685812-4b943f1cb0eb",
          },
          {
            _id: "8",
            name: "Gaming Tournament",
            date: "2026-01-15",
            image: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b",
          },
        ];

        setEvents(sampleEvents);
      } else {
        console.error("Failed to fetch events.");
      }
    } catch (error) {
      console.error("Error during fetchEvents:", error);
    }
  };

  const handleBookEvent = async (eventId) => {
    try {
      const response = await fetch(`https://ticket-a8ez.onrender.com/ticket/user`, {
        headers: {
          Authorization: `${token}`,
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        console.error("Failed to fetch user tickets.");
        return;
      }

      const data = await response.json();
      const userTickets = data.userTickets;
      const isAlreadyBooked = userTickets.some((ticket) => ticket.event._id === eventId);

      if (isAlreadyBooked) {
        setShowAlreadyBookedPopup(true);
      } else {
        const bookResponse = await fetch(`https://ticket-a8ez.onrender.com/ticket`, {
          method: "POST",
          headers: {
            Authorization: `${token}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ eventId }),
        });

        if (bookResponse.ok) {
          setShowSuccessPopup(true);
        } else {
          console.error("Failed to book event.");
        }
      }
    } catch (error) {
      console.error("Error during event booking:", error);
    }
  };

  return (
    <div className="events">
      {/* Navbar */}
      <nav className="navbar">
        <div className="left">
          <Link to={"/dashboard"}>
            <img src="your-logo.png" alt="Logo" className="logo" />
          </Link>
        </div>
        <div className="right">
          <button onClick={openUserProfileModal}>Profile</button>
        </div>
      </nav>

      {/* Events Section */}
      <div className="events-container">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event._id} className="event-card">
              <img src={event.image} alt={event.name} className="event-image" />
              <h3>{event.name}</h3>
              <p>Date: {formatDate(event.date)}</p>
              <button onClick={() => handleBookEvent(event._id)}>Book Now</button>
            </div>
          ))
        ) : (
          <p>No events available</p>
        )}
      </div>

      {showSuccessPopup && (
        <div className="popup success">
          <p>Event booked successfully!</p>
          <button onClick={() => setShowSuccessPopup(false)}>Close</button>
        </div>
      )}

      {showAlreadyBookedPopup && (
        <div className="popup error">
          <p>You have already booked this event.</p>
          <button onClick={() => setShowAlreadyBookedPopup(false)}>Close</button>
        </div>
      )}

      {showUserProfileModal && <UserProfileModel user={user} onClose={closeUserProfileModal} />}
    </div>
  );
}

export default Events;
