import React, { useState, useEffect } from "react";
import "./UserProfileModel.css";

function UserProfileModel({ onClose, onEdit }) {
  const defaultProfilePic = "https://i.imgur.com/3pQEg0A.png"; // Replace with an actual "No Profile Pic" image

  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedEmail = localStorage.getItem("email");
    const storedName = localStorage.getItem("username");
    let storedProfilePic = localStorage.getItem("profilePic");

    // Use default "No Profile Pic" image if none is found or if the URL is invalid
    if (!storedProfilePic) {
      storedProfilePic = defaultProfilePic;
    }

    if (storedEmail && storedName) {
      setUser({ email: storedEmail, name: storedName, profilePic: storedProfilePic });
    }
  }, []);

  return (
    <div className="modal-overlay">
      <div className="user-profile-modal">
        <h2>User Profile</h2>

        {user ? (
          <>
            <div className="profile-section">
              <img 
                src={user.profilePic} 
                alt="User Profile" 
                className="profile-pic" 
                onError={(e) => e.target.src = defaultProfilePic} // Fallback if the image fails to load
              />
              <div className="user-info">
                <p><strong>Name:</strong> {user.name}</p>
                <p><strong>Email:</strong> {user.email}</p>
              </div>
            </div>

            <div className="button-group">
              <button className="edit-btn" onClick={onEdit}>Edit Profile</button>
              <button className="close-btn" onClick={onClose}>Close</button>
            </div>
          </>
        ) : (
          <p>Loading user profile...</p>
        )}
      </div>
    </div>
  );
}

export default UserProfileModel;
